const header = document.querySelector("header");
const cursor = document.querySelector("#move-up");

const obsever = new IntersectionObserver((entries, obsever) => {
  entries.forEach((e) => {
    if (e.target === header) {
      console.log(cursor);
      cursor.classList.toggle("disappear");
    }
  });
}, {});

obsever.observe(header);
